<script>
    function myFunction(text) {
        if (text == '' || text === '' || text == null) {
            $("#search_result").addClass('active').html(`
                    <li class="p-2 text-center">
                        اكتب اسم المكان لبدء البحث
                    </li>`);
        } else {
            if (text.length > 0) {
                $.ajax({
                    url: "{{ route('front.searchPlaces') }}",
                    type: "POST",
                    data: {
                        search: text,
                        _token: $('meta[name="csrf-token"]').attr('content')
                    },
                    success: function(response) {
                        if (response.length > 0 && text.length > 0) {
                            let result = '';
                            response.forEach(element => {
                                result += `<li class="search-form__results-card">
                                    <a class="d-flex align-items-center" href="{{ url('') }}/${element.type}-details/${element.id}">
                                        <div>
                                            <img src="{{ asset('') }}` + element.image + `" alt="اسم المكان">
                                        </div>
                                        <div>
                                            <h4 class="head">${element.title}</h4>
                                            <p class="mb-0">
                                                ${element.description}
                                            </p>
                                        </div>
                                    </a>
                                </li> `
                            });
                            $("#search_result").addClass('active').html(result)
                        } else {
                            $("#search_result").addClass('active').html(`
                                <li class="p-2 text-center">
                                    لا يوجد اماكن متاحة
                                </li>`);
                        }
                    },
                    error(data) {
                        $("#search_result").removeClass('active')
                    }
                });
            } else {
                $("#search_result").addClass('active').html(`
                    <li class="p-2 text-center">
                        اكتب اسم المكان لبدء البحث
                    </li>`);
            }
        }
    }

    function addOpinion() {
        var name = $('#name').val();
        var email = $('#email').val()
        var phone = $('#phone').val()
        var message = $('#message').val()

        if (name == '' || name == null || name == ' ') {
            $('#u_name').show();
        }
        if (email == '' || email == null || email == ' ') {
            $('#u_email').show();
        }
        if (phone == '' || phone == null || phone == ' ') {
            $('#u_phone').show();
        }
        if (message == '' || message == null || message == ' ') {
            $('#u_message').show();
        }

        if ($('#name').val() != '' && $('#email').val() != '' && $('#phone').val() != '' && $('#message').val() != '') {
            $.ajax({
                url: "{{ route('front.send') }}",
                type: "POST",
                data: {
                    name: $('#name').val(),
                    email: $('#email').val(),
                    phone: $('#phone').val(),
                    message: $('#message').val(),
                    _token: $('meta[name="csrf-token"]').attr('content')
                },
                success: function(response) {
                    $('#msgForm').trigger("reset");
                    $('#u_name').hide();
                    $('#u_email').hide();
                    $('#u_phone').hide();
                    $('#u_message').hide();

                    toastr.success('تم الارسال بنجاح')

                },
                error(data) {}
            });
        }
    }
</script>
